import React from "react";
import { useForm, SubmitHandler, FieldValues } from "react-hook-form";
import styled from "styled-components";
import axios from "axios";

const StyledWrapper = styled.div`
  padding: 20px;
`;

const StyledTable = styled.table`
  width: 100%;
  border-collapse: collapse;
`;

const StyledTableHeader = styled.th`
  border: 1px solid #ddd;
  padding: 8px;
  background-color: #f2f2f2;
`;

const StyledTableRow = styled.tr`
  border: 1px solid #ddd;
`;

const StyledTableData = styled.td`
  border: 1px solid #ddd;
  padding: 8px;
`;

const StyledInput = styled.input`
  width: 100%;
  padding: 6px;
  box-sizing: border-box;
`;

interface TableProps<T extends FieldValues> {
  data: T[];
  onSubmitSuccess: () => void;
}

function Table<T extends FieldValues>({ data, onSubmitSuccess }: TableProps<T>): JSX.Element {
  const { register, handleSubmit, setValue } = useForm<T>();

  const onSubmit: SubmitHandler<T> = async (formData) => {
    try {
      await axios.post("/api/update-data", formData);
      onSubmitSuccess();
    } catch (error) {
      console.error("Error updating data:", error);
    }
  };

  const tableHeaders = data.length > 0 ? Object.keys(data[0]) : [];

  return (
    <StyledWrapper>
      <form onSubmit={handleSubmit(onSubmit)}>
        <StyledTable>
          <thead>
            <StyledTableRow>
              {tableHeaders.map((header) => (
                <StyledTableHeader key={header}>{header}</StyledTableHeader>
              ))}
            </StyledTableRow>
          </thead>
          <tbody>
            {data.map((item, rowIndex) => (
              <StyledTableRow key={rowIndex}>
                {tableHeaders.map((header) => (
                  <StyledTableData key={`${rowIndex}-${header}`}>
                    <StyledInput
                      defaultValue={String(item[header as keyof T])}
                      {...register(`${header}` as any, {
                        value: item[header as keyof T]
                      })}
                      onChange={(e) =>
                        setValue(`${header}` as any, e.target.value as any)
                      }
                    />
                  </StyledTableData>
                ))}
              </StyledTableRow>
            ))}
          </tbody>
        </StyledTable>
        <button type="submit">Save Changes</button>
      </form>
    </StyledWrapper>
  );
}

export default Table;
