import styled, { keyframes, css } from "styled-components";

export const StyledWrapper = styled.div`
  display: flex;
  width: 100%;
`;

export const StyledTable = styled.table`
  background-color: ${(props) => props.theme.colors.background};
  font-family: Arial, Helvetica, sans-serif;
`;

export const StyledTableHeader = styled.th`
  padding-top: 12px;
  padding-bottom: 12px;
  text-align: left;
  background-color: ${(props) => props.theme.colors.border};
  padding: 0.5rem;
`;

export const StyleTableRow = styled.tr``;

export const StyleFlipTbody = styled.tbody`
  background-color: transparent;
`;

const flipAnimation = keyframes`
  0% {
    transform: rotateX(0deg);
  }
  100% {
    transform: rotateX(180deg);
  }
`;

const flipBackAnimation = keyframes`
  0% {
    transform: rotateX(180deg);
  }
  100% {
    transform: rotateX(0deg);
  }
`;

export const StyleFlipTableRowInner = styled.tr<{ $isflipped: boolean }>`
  position: relative;
  height: 100%;
  text-align: center;
  transition: transform 0.9s;
  /* transform-style: preserve-3d; */
  box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2);
  /* transform: ${({ $isflipped }) => ($isflipped ? "rotateX(180deg)" : "rotateX(0)")}; */
  border: ${({ $isflipped }) => (!$isflipped && "0.5")};

`;

export const StyledFlipTableDataFront = styled.td<{ $isflipped: boolean }>`
  border: 1px solid #dddddd;
  text-align: left;
  padding: 0.5rem;
  -webkit-backface-visibility: hidden;
  backface-visibility: hidden;
  background-color: #bbb;
  color: black;
  /* opacity: ${({ $isflipped }) => (!$isflipped && "0.5")}; */
  /* opacity: 0.5; */

  
`;

export const StyledFlipTableDataBack = styled.td<{ $isflipped: boolean }>`
  border: 1px solid #dddddd;
  text-align: left;
  padding: 0.5rem;
  -webkit-backface-visibility: hidden;
  backface-visibility: hidden;
  background-color: #2980b9;
  color: white;
  /* transform-style: preserve-3d; */

  animation: ${({ $isflipped }) =>
    $isflipped
      ? css`
          ${flipBackAnimation} 2.5s forwards
        `
      : css`
          
          ${flipAnimation} 2.5s forwards
        `};
`;

export const StyledInput = styled.input`
  display: flex;
  width: 100%;
`;
