import styled from "styled-components";

export const StyledWrapper = styled.div`
  display: flex;
  width: 100%;
`;

export const StyledTable = styled.table`
  background-color: ${(props) => props.theme.colors.background};
  font-family: Arial, Helvetica, sans-serif;
`;

export const StyledTableHeader = styled.th`
  padding-top: 12px;
  padding-bottom: 12px;
  text-align: left;
  background-color: ${(props) => props.theme.colors.border};
  padding: 0.5rem;
`;

export const StyleTableRow = styled.tr``;

export const StyleFlipTbody = styled.tbody`
  background-color: transparent;
  width: 100%;
  perspective: 1000px;
`;

export const StyleFlipTableRowInner = styled.tr`
  position: relative;
  width: 100%;
  height: 100%;
  text-align: center;
  transition: transform 0.6s;
  transform-style: preserve-3d;
  box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2);
  transform: ${(props) => (props["data-isflipped"] ? "rotateY(180deg)" : "rotateY(0)")};
`;

export const StyledFlipTableDataFront = styled.td`
  position: absolute;
  width: 100%;
  height: 100%;
  -webkit-backface-visibility: hidden;
  backface-visibility: hidden;
  background-color: #bbb;
  color: black;
`;

export const StyledFlipTableDataBack = styled.td`
  position: absolute;
  width: 100%;
  height: 100%;
  -webkit-backface-visibility: hidden;
  backface-visibility: hidden;
  background-color: #2980b9;
  color: white;
  transform: rotateY(180deg);
`;
