import { useEffect } from "react";
import { useDispatch } from "react-redux";
import { io } from "socket.io-client";
import SocketEvents from "src/Models/SocketEvents";
import { addSocialCustomer } from "src/redux/features/socialCustomer/socialCustomerSlice";

function useSocketIo(url: string, socketEvent: SocketEvents): any {
  const dispatch = useDispatch();

  useEffect(() => {
    const socket = io(url);
     socket.on(socketEvent, (dataFromServer) => {
      dispatch((dataFromServer));
    });addSocialCustomer
    return () => {
      socket.disconnect();
    };
  }, [url]);
}

export default useSocketIo;
